contracts/Trustified-NonTransferable.sol.content:
// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

import "./comman/FERC721URIStorage.sol";

contract TrustifiedNonTransferable is ERC721URIStorage {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;
    Counters.Counter private _eventIdCounter;

    mapping(uint256 => uint256[]) public tokenIds;

    mapping(uint256 => bool) private transferStatus;

    event TokenMinted(address, uint256);

    constructor() ERC721("Trustified", "TFN") {}

    function safeMint() public returns (uint256) {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _mint(address(this), tokenId);
        return tokenId;
    }

     function safeMintBadge(string calldata tokenURI) public returns (uint256) {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _mint(address(this), tokenId);
        _setTokenURI(tokenId, tokenURI);
        return tokenId;
    }

    function bulkMintERC721(uint256 quantity) external {
        uint256 eventId = _eventIdCounter.current();
        _eventIdCounter.increment();
        for (uint256 i = 0; i < quantity; i++) {
            uint256 tokenId = safeMint();
            tokenIds[eventId].push(tokenId);
        }
        emit TokenMinted(msg.sender, eventId);
    }


    function bulkMintBadgesERC721(string calldata tokenUri, uint256 quantity) external {
        uint256 eventId = _eventIdCounter.current();
        _eventIdCounter.increment();
        for (uint256 i = 0; i < quantity; i++) {
            uint256 tokenId = safeMintBadge(tokenUri);
            tokenIds[eventId].push(tokenId);
        }
        emit TokenMinted(msg.sender, eventId);
    }

    function getTokenIds(uint256 eventId)
        external
        view
        returns (uint256[] memory)
    {
        return tokenIds[eventId];
    }

    function transferToken(
        address from,
        address to,
        uint256 tokenId,
        string calldata tokenURI
    ) external {
        require(
            transferStatus[tokenId] != true,
            "This token can not be transferred!"
        );
        _setTokenURI(tokenId, tokenURI);
        IERC721(address(this)).transferFrom(from, to, tokenId);
        transferStatus[tokenId] = true;
    }

      function transferBadgesToken(address from, address to, uint256 tokenId) external {
        require(
            transferStatus[tokenId] != true,
            "This token can not be transferred!"
        );
        IERC721(address(this)).transferFrom(from, to, tokenId);
        transferStatus[tokenId] = true;
    }
}

contracts/Trustified.sol.content:
// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

import "./comman/FERC721URIStorage.sol";

contract Trustified is ERC721URIStorage {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;
    Counters.Counter private _eventIdCounter;

    mapping(uint256 => uint256[]) public tokenIds;

    event TokenMinted(address, uint256);

    constructor() ERC721("Trustified", "TFN") {}

    function safeMint(
        string calldata tokenURI,
        uint256 value
    ) public returns (uint256) {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _mint(address(this), tokenId);
        if (value == 0) {
            _setTokenURI(tokenId, tokenURI);
        }
        return tokenId;
    }

    function bulkMintERC721(
        string calldata tokenUri,
        uint256 quantity,
        uint256 value
    ) external {
        uint256 eventId = _eventIdCounter.current();
        _eventIdCounter.increment();
        for (uint256 i = 0; i < quantity; i++) {
            uint256 tokenId = safeMint(tokenUri, value);
            tokenIds[eventId].push(tokenId);
        }
        emit TokenMinted(msg.sender, eventId);
    }

    function getTokenIds(
        uint256 eventId
    ) external view returns (uint256[] memory) {
        return tokenIds[eventId];
    }

    function transferToken(
        address from,
        address to,
        uint256 tokenId,
        string calldata tokenURI,
        uint256 value
    ) external {
        if (value == 1) {
            _setTokenURI(tokenId, tokenURI);
        }
        IERC721(address(this)).transferFrom(from, to, tokenId);
    }
}

contracts/TrustifiedCred.sol.content:
// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@creds-protocol/contracts/CredsIssuer.sol";

contract TrustifiedCred is CredsIssuer {
    using Counters for Counters.Counter;
    Counters.Counter private _credIds;

    address public owner;

    event CredCreated(address, uint256, uint256, uint256, string);
    event IssueCred(address, uint256, uint256);

    constructor(
        Verifier[] memory _verifiers,
        address _issuer,
        string memory _issuerName,
        string memory _issuerSymbol
    ) CredsIssuer(_verifiers, _issuer, _issuerName, _issuerSymbol) {}

    function issueCred(
        uint256 merkleTreeDepth,
        uint256 zeroValue,
        string memory credURI
    ) public {
        uint256 _credId = _credIds.current();
        _credIds.increment();
        createCred(_credId, merkleTreeDepth, zeroValue, msg.sender, credURI);
        emit CredCreated(
            msg.sender,
            _credId,
            merkleTreeDepth,
            zeroValue,
            credURI
        );
    }

    function claimCredToTrustifiedIssuer(
        uint256 credId,
        uint256 identityCommitment
    ) public {
        addIdentity(credId, identityCommitment);
    }
}

contracts/TrustifiedCreds.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./TrustifiedCred.sol";
import "@creds-protocol/contracts/interfaces/ICredential.sol";


contract TrustifiedCreds is ICredential {
    mapping(address => address[]) private credIssuers;

    event createIssuer(address, address);

    function createCredIssuer(
        Verifier[] memory _verifiers,
        address _issuer,
        string memory _issuerName,
        string memory _issuerSymbol
    ) public {
        address _address = address(
            new TrustifiedCred(_verifiers, _issuer, _issuerName, _issuerSymbol)
        ); 

        credIssuers[msg.sender].push(_address);
        emit createIssuer(msg.sender, _address);
    }
}

